        private static string SectionTemplate = @"<p style=""text-align:justify""><span style=""font-size:18px""><strong>[SectionName]</strong></span></p>";
        private static string SectionRepeatableTemplate = @"<p style=""text-align:justify""><span style=""font-size:18px""><strong>#[Index] - [SectionName]</strong></span></p>";
        private static string AnswerTemplate = @"<p style=""margin-left:40px; text-align:justify""><span style=""font-size:16px""><strong>[FieldName]:&nbsp;</strong></span><strong>[FieldAnswer]</strong></p>";


        private static string GetSectionRecursive(Form form, FormSectionId currentSection, Subscription subscription)
        {
            var section = form.Sections.FirstOrDefault(s => s.Id.Equals(currentSection));
            if (section is null)
                return "";

            var nextQuesntion = section.DefaultNextSection;

            //string resultTempalte = "";
            var orderedFields = section.Fields.OrderBy(f => f.Order);
            
            var previousIndex = -1;

            List<string> resultTempalte = new List<string>();

            foreach (var field in orderedFields)
            {
                if (field.Type.Equals(FieldType.Information)) continue;

                var answers = subscription.Answers.Where(a => a.FieldId.Equals(field.Id));

                if (!answers.Any()) continue;

                foreach (var ans in answers.OrderBy(a => a.Index))
                {
                    //if (previousIndex != ans.Index)
                    //{
                    //    previousIndex = ans.Index;
                    //    if(!section.Repeatable.CanRepeat)
                    //        resultTempalte = SectionTemplate.Replace("[SectionName]", section.Name);
                    //    else
                    //        resultTempalte = SectionRepeatableTemplate.Replace("[SectionName]", section.Name).Replace("[Index]", ans.Index.ToString());
                    //}
                    if (resultTempalte.Count < ans.Index) resultTempalte.Add("");

                    resultTempalte[ans.Index - 1] = resultTempalte[ans.Index-1] + AnswerTemplate.Replace("[FieldName]", field.Description).Replace("[FieldAnswer]", ans.Value.Value);

                    if (field.Type.Equals(FieldType.Options))
                        nextQuesntion = field.OptionsSettings.Options.FirstOrDefault(o => o.Value == ans.Value.OptionsValue)?.NextSection ?? nextQuesntion;
                    else if (field.Type.Equals(FieldType.Choice))
                        nextQuesntion = field.ChoiceSettings.NextSection ?? nextQuesntion;
                }
            }

            var result = "";

            if (resultTempalte.Count == 1)
            {
                result += SectionTemplate.Replace("[SectionName]", section.Name);
                result += resultTempalte[0];
            }
            else
            {
                for (int i = 0; i < resultTempalte.Count; i++)
                {
                    result += SectionRepeatableTemplate.Replace("[SectionName]", section.Name).Replace("[Index]", (i+1).ToString());
                    result += resultTempalte[i];
                }
            }

            if (nextQuesntion is null)
                return result;

            return result + GetSectionRecursive(form, nextQuesntion, subscription);
        }
